import { PayPalButton } from "react-paypal-button-v2"
import {Elements} from '@stripe/react-stripe-js';
import {PaymentElement} from '@stripe/react-stripe-js'
import {loadStripe} from '@stripe/stripe-js';
import React, {Component} from 'react'
import ApiService from "../../services/api"
import { Navigate } from 'react-router-dom'
import Loading from '../loading/loading'
import configs from "../../../configs.json"

const apiservice = new ApiService()
const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY)

class Subscribe extends Component {
    constructor(props) {
        super(props)
        this.state = {
            auth    : null,
            plan    : process.env.REACT_APP_PLAN_ID,
            client  : process.env.REACT_APP_CLIENT_ID,
            amount  : 10.0,
            currency: "BRL",
            vault   : true,
            style   : {
                shape: 'rect',
                color: 'gold',
                layout: 'horizontal',
                label: 'subscribe',
                },
        }
    }

    componentDidMount() {
        const token = localStorage.getItem('token')
        if (token !== null) {
            const user  = localStorage.getItem('user')
            const data  = new FormData()

            data.append('token', token)
            data.append('user', user)
            data.append('role', 'None')

            apiservice.hasAuth(data).then((r) => {
                console.log(`Response: ${r}`)
                this.setState({auth: true})

            }).catch((e) => {
                console.log(`Error on request: ${e}`)
                this.setState({auth: false})
            })
        } else {
            this.setState({auth: false})
        }
    }

    render() {
        const paypalSubscribe = (data, actions) => {
            return actions.subscription.create({'plan_id': this.state.plan,})
        }

        const paypalOnError = (err) => {
            console.log('Error loading Paypal script!', err);
        }

        const paypalOnCancel = (data) => {
            console.log('The payment was cancelled!', data)
        }

        const paypalOnApprove = (data, detail) => {
            //todo: call the backend api to store transaction details
            console.log("Payapl approved", data.subscriptionID)
        };

        const options = {
            mode: 'payment',
            currency: 'usd',
            amount: 1099,
        }

        if (this.state.auth === true) {
            return(
                <div className="container center" style={{backgroundColor: configs.colors.light}}>
                    <div className="row section-row">
                        <div className="card" style={{backgroundColor: configs.colors.offwhite}}>
                            <div className="card-title" style={{color: configs.colors.primary}}>
                                <h3>Pagamento</h3>
                                <p style={{color: configs.colors.dark, fontSize: '16px'}}>Assine e tenha acesso ao nosso conteudo!</p>
                            </div>
                            <div className="card-content">
                                <Elements stripe={stripePromise} options={options}>
                                    <form>
                                        <PaymentElement />
                                        <button className="btn" style={{backgroundColor: configs.colors.prima}}>Submit</button>
                                    </form>
                                </Elements>
                            </div>
                        </div>
                    </div>
                </div>

            )
        } else {
            if (this.state.auth === false) {
                return(
                    <Navigate to='/' />
                )
            } else {
                return (
                    <Loading/>
                )
            }
        }
    }
}

export default Subscribe;
