import React, { Component } from "react";
import ApiService from "../../services/api";
import configs from "../../../configs.json"

const apiservice = new ApiService()

class Profile extends Component  {
    constructor(props) {
        super(props)
        this.state = {
            name: '',
            status: '',
            email: ''
        }
    }
    componentDidMount() {
        const user  = localStorage.getItem('user')
        const token = localStorage.getItem('token')
        const data  = new FormData()

        data.append('user', user)
        data.append('token', token)
        data.append('role', 'None')

        apiservice.getUser(data).then((r) => {
            this.setState({name: r.user, status: r.status, email: r.email})
        }).catch((e) => {
            alert('Network Error!')
        })
    }
    render() {
        return(
            <div className="container" style={{backgroundColor: configs.colors.light}}>
                <div className="card horizontal" style={{backgroundColor: configs.colors.offwhite}}>
                    <div className="card-stacked">
                        <div className="card-content left-align">
                            <h6>Name: {this.state.name}</h6>
                            <h6>Subscription status: {this.state.status}</h6>
                            <h6>Email: {this.state.email}</h6>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
/*
<div className="card-action right-align">
    <a href="/save_profile/">Save</a>
</div>
*/

export default Profile;
