import { PayPalButton } from "react-paypal-button-v2"
import React, {Component} from 'react'
import ApiService from "../../services/api"
import { Navigate } from 'react-router-dom'
import Loading from '../loading/loading'
import configs from "../../../configs.json"

const apiservice = new ApiService()

class Subscribe extends Component {
    constructor(props) {
        super(props)
        this.state = {
            auth    : null,
            plan    : process.env.REACT_APP_PLAN_ID,
            client  : process.env.REACT_APP_CLIENT_ID,
            amount  : 10.0,
            currency: "BRL",
            vault   : true,
            style   : {
                shape: 'rect',
                color: 'gold',
                layout: 'horizontal',
                label: 'subscribe',
                },
        }
    }

    componentDidMount() {
        const token = localStorage.getItem('token')
        if (token !== null) {
            const user  = localStorage.getItem('user')
            const data  = new FormData()

            data.append('token', token)
            data.append('user', user)
            data.append('role', 'None')

            apiservice.hasAuth(data).then((r) => {
                console.log(`Response: ${r}`)
                this.setState({auth: true})

            }).catch((e) => {
                console.log(`Error on request: ${e}`)
                this.setState({auth: true})
            })
        } else {
            this.setState({auth: true})
        }
    }

    render() {
        const paypalSubscribe = (data, actions) => {
            return actions.subscription.create({'plan_id': this.state.plan,})
        }

        const paypalOnError = (err) => {
            console.log('Error loading Paypal script!', err);
        }

        const paypalOnCancel = (data) => {
            console.log('The payment was cancelled!', data)
        }

        const paypalOnApprove = (data, detail) => {
            //todo: call the backend api to store transaction details
            console.log("Payapl approved", data.subscriptionID)
        };

        if (this.state.auth === true) {
            return(
                <div className="container center" style={{background}}>
                    <div className="">
                        <h1 className="header orange-text">
                            Assine e tenha acesso ao nosso conteudo!
                        </h1>
                    </div>
                    <div className="row section-row">
                        <div className="card" style={{backgroundColor: configs.colors.offwhite}}>
                            <PayPalButton
                                amount             = {this.state.amount}
                                currency           = {this.state.currency}
                                createSubscription = {(data, details) => paypalSubscribe(data, details)}
                                onApprove          = {(data, details) => paypalOnApprove(data, details)}
                                onError            = {(err) => paypalOnError(err)}
                                catchError         = {(err) => paypalOnError(err)}
                                onCancel           = {(err) => paypalOnCancel(err)}
                                options            = {{ clientId: this.state.client, vault: this.state.vault}}
                                style              = {this.state.style}
                            />
                        </div>
                    </div>
                </div>
            )
        } else {
            if (this.state.auth === false) {
                return(
                    <Navigate to='/' />
                )
            } else {
                return (
                    <Loading/>
                )
            }
        }
    }
}

export default Subscribe;
