import React, {Component} from "react";
import { Navigate } from 'react-router-dom'
import ApiService from "../../services/api";

const apiservice = new ApiService()

class Login extends Component {
    constructor(props) {
        super(props)
        this.state = {
            username : "",
            password : "",
            redirect  : false,
            highlight : this.props.highlight
        }
        this.handleSubmit = this.handleSubmit.bind(this)
    }

    handleSubmit(event) {
        event.preventDefault()
        const data = new FormData()

        data.append("username", this.state.username)
        data.append("password", this.state.password)

        apiservice.login(data).then((r) => {
            localStorage.setItem('token', r.token)
            localStorage.setItem('user', this.state.username)
            this.setState({redirect: true})
        }).catch((e) => {
            alert('Something went wrong!')
            console.log(e)
        })
    }

    render() {
        if (this.state.redirect === false) {
            return(
                <div class="container center">
                    <div className="card amber darken-2 center-align">
                        <div className="card-title">
                            <h4>Login</h4>
                        </div>
                        <form method="POST" encType="multipart/form-data" onSubmit={this.handleSubmit}>
                            <div className="card-content white-text">
                                <div className="center">
                                    <div className="row">
                                        <label for="input_text">Usuario</label>
                                        <input id="input_text" type="text" ref="username" placeholder="User123" onChange={(e) => {this.setState({username: e.target.value})}}/>
                                    </div>
                                </div>
                                <div className="center">
                                    <div className="row">
                                        <label for="input_text">Senha</label>
                                        <input id="input_text" type="password" ref="password" placeholder="********" onChange={(e) => {this.setState({password: e.target.value})}}/>
                                    </div>
                                </div>
                            </div>
                            <div className="card-action">
                                <button type='submit' className="btn waves-effect waves-light orange darken-3">Entrar<i className="material-icons right">send</i></button>
                            </div>
                        </form>
                    </div>
                </div>
            )
        } else {
            return(
                <Navigate to='/' />
            )
        }
    }
}

export default Login;
