import React, { Component } from "react";
import ApiService from "../../services/api";
import configs from "../../../configs.json"

const apiservice = new ApiService()

class Profile extends Component  {
    constructor(props) {
        super(props)
        this.state = {
            name: '',
            status: '',
            email: ''
        }
    }
    componentDidMount() {
        const user  = localStorage.getItem('user')
        const token = localStorage.getItem('token')
        const data  = new FormData()

        data.append('user', user)
        data.append('token', token)
        data.append('role', 'None')

        apiservice.getUser(data).then((r) => {
            this.setState({name: r.user, status: r.status, email: r.email})
        }).catch((e) => {
            alert('Network Error!')
        })
    }
    render() {
        if (this.state.auth === true) {
        return(
            <div className="container" style={{backgroundColor: configs.colors.light}}>
                <div className="card horizontal" style={{backgroundColor: configs.colors.offwhite}}>
                    <div className="card-stacked">
                        <div className="card-title" style={{color: configs.colors.primary}}>
                            <h3>Perfil</h3>
                            <p style={{color: configs.colors.dark, fontSize: '16px'}}>Informacoes vinculadas a sua conta</p>
                        </div>
                        <div className="card-content left-align">
                            <h6>Nome: {this.state.name}</h6>
                            <h6>Status da Inscricao: {this.state.status}</h6>
                            <h6>Email: {this.state.email}</h6>
                        </div>
                        <div className="card-action">
                            <div className="row" style={{margin: '0px'}}>
                                <div className="col s6">
                                    <a href="/save_profile/" style={{margin: '0px'}}>Mudar<br/>Email</a>
                                </div>
                                <div className="col s6">
                                    <a href="/save_profile/" style={{margin: '0px'}}>Mudar<br/>Senha</a>
                                </div>
                            </div>
                        </div>
                        <div className="card-action">
                            <div className="row" style={{margin: '0px'}}>
                                <div className="col s12">
                                    <a href="/save_profile/" style={{margin: '0px'}}>Deletar Conta</a>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
    }
}

export default Profile;
